import { useQuery } from '@apollo/client';
import { SectionItem, DataTable, Link } from 'ui';
import Description from './Description';
import TEST_WIDGET_QUERY from './TestWidgetQuery.gql';

const columns = [
  { id: 'name', label: 'Name' },
  { id: 'score', label: 'Score', numeric: true },
  { id: 'source', label: 'Source' },
];

function Body({ definition, id, label }) {
  const request = useQuery(TEST_WIDGET_QUERY, { variables: { ensemblId: id } });
  return (
    <SectionItem
      definition={definition}
      request={request}
      renderDescription={() => <Description symbol={label} />}
      renderBody={(data) => {
        const rows = data.target.testWidget.rows;
        return <DataTable columns={columns} rows={rows} />;
      }}
    />
  );
}

export default Body;

