import { usePlatformApi } from 'hooks/usePlatformApi';
import { SummaryItem } from 'ui';
import TEST_WIDGET_SUMMARY from './TestWidgetSummaryFragment.gql';

function Summary({ definition }) {
  const request = usePlatformApi(TEST_WIDGET_SUMMARY);
  return (
    <SummaryItem
      definition={definition}
      request={request}
      renderSummary={(data) => data.testWidget.count + ' entries'}
    />
  );
}

Summary.fragments = { TestWidgetSummaryFragment: TEST_WIDGET_SUMMARY };
export default Summary;
